        public ProductControllerTests(ApiWebApplicationFactory fixture) : base(fixture)
        {
            
        }

        [Fact]
        public async Task Get_All_Should_Return_NotEmptyList()
        {
            //arrange
            //act
            var response = await client.GetAsync("api/Product/GetAll");

            //assert
            response.StatusCode.Should().Be(HttpStatusCode.OK);
            var responseBody = await response.Content.ReadAsStringAsync();
            var Products = JsonConvert.DeserializeObject<List<Product>>(responseBody);
            Products.Should().NotBeEmpty();

        }

        [Fact]
        public async Task Create_Product_Should_Return_ProductId()
        {
            //Arrange
            var newProduct = new Product()
            {
                ProductName = "TestName",
                ProductDescription = "TestDescription",
                ProductPrice = 1,
                ProductWeight = 1,
                ProductQuantityInStock = 1,
                IdCategory = 1,
                IdManufacturer = 4,
            };
            var hex =
                "";
            var bytes = Enumerable.Range(0, hex.Length)
                .Where(x => x % 2 == 0)
                .Select(x => Convert.ToByte(hex.Substring(x, 2), 16))
                .ToArray();
            newProduct.ProductImages.Add(new ProductImage() { Picture = bytes });

            var content = new StringContent(JsonConvert.SerializeObject(newProduct.AdaptToDto()), Encoding.UTF8, "application/json");
            //Act
            var response = await client.PostAsync("api/Product/ProductCreate/", content);
            //Assert
            response.StatusCode.Should().Be(HttpStatusCode.OK);
            var newProductId = JsonConvert.DeserializeObject<int>(await response.Content.ReadAsStringAsync());
            newProductId.Should().NotBe(0);
        }

        [Fact]
        public async Task Edit_Should_Return_EditedProductName()
        {
            //Arrange
            var product = new Product()
            {
                Id = 3,
                ProductName = DateTime.Now.ToString(),
                ProductDescription = DateTime.Now.ToString(),
                ProductPrice = 1,
                ProductWeight = 1,
                ProductQuantityInStock = 1,
                IdCategory = 1,
                IdManufacturer = 4,
            };

            var hex =
    "";
            var bytes = Enumerable.Range(0, hex.Length)
                .Where(x => x % 2 == 0)
                .Select(x => Convert.ToByte(hex.Substring(x, 2), 16))
                .ToArray();
            product.ProductImages.Add(new ProductImage() { Picture = bytes });

            var content = new StringContent(JsonConvert.SerializeObject(product.AdaptToDto()), Encoding.UTF8, "application/json");

            //Act
            var response = await client.PutAsync("api/Product/ProductEdit/", content);

            //Assert
            response.StatusCode.Should().Be(HttpStatusCode.OK);
        }
        [Fact]
        public async Task Edit_Should_ResultInInternalServerError()
        {
            //Arrange
            var product = new Product()
            {
                Id = 9,
                ProductName = null,
                ProductDescription = DateTime.Now.ToString(),
                ProductPrice = 1,
                ProductWeight = 1,
                ProductQuantityInStock = 1,
                IdCategory = 1,
                IdManufacturer = 4,
            };


            var content = new StringContent(JsonConvert.SerializeObject(product), Encoding.UTF8, "application/json");

            //Act
            var response = await client.PutAsync("api/Product/Edit/", content);

            //Assert
            response.StatusCode.Should().Be(HttpStatusCode.InternalServerError);
        }
