        [Fact]
        public async Task Delete_Of_NonExisting_Product_Id_Should_ResultInInternalServerError()
        {
            // Arrage

            int productIdToDelete = 0;

            // Act
            var response = await client.DeleteAsync($"api/Product/Delete/?id={productIdToDelete}");

            // Assert
            response.StatusCode.Should().Be(HttpStatusCode.InternalServerError);
        }
        [Fact]
        public async Task Delete_Using_Not_Valid_Product_Id_Should_ResultInBadRequest()
        {
            // Act
            var response = await client.DeleteAsync($"api/Product/Delete/?id=aaaaaa");

            // Assert
            response.StatusCode.Should().Be(HttpStatusCode.BadRequest);
